import React, {useState} from 'react';
import {useActions} from "../../hooks/useActions";
import {useSelector} from "react-redux";
import imgDefault from "../../img/default-img.png";
import imgRatingStar from "../../img/star-rate.svg";

const CreateMemory = () => {
    const {isMemoryCardModal, selectedMemories, selectedCategory} = useSelector(state => state.memoryReducer)
    const {addMemory, setIsMemoryCardModal} = useActions()

    const [memoryNameValue, setMemoryNameValue] = useState('')
    const [memoryRateValue, setMemoryRateValue] = useState(0)
    const [memoryImgValue, setMemoryImgValue] = useState(imgDefault)
    const [isWarning, setIsWarning] = useState(false)
    const [warningValue, setWarningValue] = useState('')

    const [ratingFive, setRatingFive] = useState(false)
    const [ratingFour, setRatingFour] = useState(false)
    const [ratingThree, setRatingThree] = useState(false)
    const [ratingTwo, setRatingTwo] = useState(false)
    const [ratingOne, setRatingOne] = useState(false)

    const currentRatingIsOne = () => {
        setRatingFive(false)
        setRatingFour(false)
        setRatingThree(false)
        setRatingTwo(false)
        setRatingOne(true)
        setMemoryRateValue(1)
    }
    const currentRatingIsTwo = () => {
        setRatingFive(false)
        setRatingFour(false)
        setRatingThree(false)
        setRatingOne(false)
        setRatingTwo(true)
        setMemoryRateValue(2)
    }
    const currentRatingIsThree = () => {
        setRatingFive(false)
        setRatingFour(false)
        setRatingTwo(false)
        setRatingOne(false)
        setRatingThree(true)
        setMemoryRateValue(3)
    }
    const currentRatingIsFour = () => {
        setRatingFive(false)
        setRatingThree(false)
        setRatingTwo(false)
        setRatingOne(false)
        setRatingFour(true)
        setMemoryRateValue(4)
    }
    const currentRatingIsFive = () => {
        setRatingFour(false)
        setRatingThree(false)
        setRatingOne(false)
        setRatingTwo(false)
        setRatingFive(true)
        setMemoryRateValue(5)
    }

    const closeMemoryCardModal = () => {
        setIsMemoryCardModal(false)
        setMemoryNameValue('')
    }
    const confirmAddMemory = () => {
        for (let i = 0; i < selectedMemories.length; i++) {
            if (selectedMemories[i].title === memoryNameValue) {
                setWarningValue("Така назва вже існує")
                setIsWarning(true)
                return false
            }
        }
        if (!memoryNameValue) {
            setWarningValue("Це поле є обов'язковим")
            setIsWarning(true)
            return false
        }
        setIsWarning(false)
        addMemory(memoryNameValue, memoryRateValue, memoryImgValue, selectedCategory.id)
        setMemoryNameValue('')
        setIsMemoryCardModal()
    }


    return (
        <div
            className={isMemoryCardModal ? 'memory-card__modal' : 'memory-card__modal closed'}
            onClick={closeMemoryCardModal}
        >
            <div
                className='content'
                onClick={e => e.stopPropagation()}
            >
                <div className='title'>
                    <h3>Як ви хочете назвати спогад?</h3>
                </div>
                <div className='values-box'>
                    <input
                        className='title-text'
                        type='text'
                        placeholder={'Назва спогаду'}
                        value={memoryNameValue}
                        onChange={e => setMemoryNameValue(e.target.value)}
                    />
                    <input
                        type='file'
                        accept=".jpg, .jpeg, .png, .svg"
                        onChange={e => setMemoryImgValue((e.target.files).toString())}
                    />
                    <div className='memory-rating'>
                        <input type="radio" id="star-5" name='rate-5' value="5"
                               checked={ratingFive}
                               onChange={currentRatingIsFive}
                        />
                        <label htmlFor="star-5" title="Оцінка «5»">
                            <img src={imgRatingStar} alt='' />
                        </label>
                        <input type="radio" id="star-4" name='rate-4' value="4"
                               checked={ratingFour}
                               onChange={currentRatingIsFour}
                        />
                        <label htmlFor="star-4" title="Оцінка «4»">
                            <img src={imgRatingStar} alt='' />
                        </label>
                        <input type="radio" id="star-3" name='rate-3' value="3"
                               checked={ratingThree}
                               onChange={currentRatingIsThree}
                        />
                        <label htmlFor="star-3" title="Оцінка «3»">
                            <img src={imgRatingStar} alt='' />
                        </label>
                        <input type="radio" id="star-2" name='rate-2' value="2"
                               checked={ratingTwo}
                               onChange={currentRatingIsTwo}
                        />
                        <label htmlFor="star-2" title="Оцінка «2»">
                            <img src={imgRatingStar} alt='' />
                        </label>
                        <input type="radio" id="star-1" name='rate-1' value="1"
                               checked={ratingOne}
                               onChange={currentRatingIsOne}
                        />
                        <label htmlFor="star-1" title="Оцінка «1»">
                            <img src={imgRatingStar} alt='' />
                        </label>
                    </div>
                    <div className='btn-confirm-box'>
                        <div className={isWarning ? 'warning-text' : 'warning-text closed'}>
                            {warningValue}
                        </div>
                        <button
                            className='btn-confirm'
                            onClick={confirmAddMemory}
                        >
                            Підтвердити
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CreateMemory;
