import {authSlice} from "./AuthSlice";
import {login, registration} from "../../../http/userAPI";

export const AuthActionCreator = {
    setIsAuth: (isAuth) => dispatch => {
        dispatch(authSlice.actions.setIsAuth(isAuth))
    },
    setUser: (user) => dispatch => {
        dispatch(authSlice.actions.setUser(user))
    },
    setIsError: (boolean) => dispatch => {
        dispatch(authSlice.actions.setIsError(boolean))
    },
    singIn: (email, password) => dispatch => {
        try {
            registration(email, password).then(() => {
                dispatch(AuthActionCreator.setIsError(true))
                dispatch(authSlice.actions.setRegMessage('Вітаємо, ви зареєструвалися!'))
            })
            registration(email, password).catch(error => {
                dispatch(AuthActionCreator.setIsError(true))
                dispatch(authSlice.actions.setRegMessage(error.response.data.message))
            })
        } catch (e) {
            console.log(e)
        }
    },
    logIn: (email, password) => async dispatch => {
        try {
            login(email, password).then(data => {
                dispatch(AuthActionCreator.setIsAuth(true))
                dispatch(AuthActionCreator.setUser(data))
                localStorage.setItem('auth', JSON.stringify(true))
                localStorage.setItem('user', JSON.stringify(data))
                dispatch(authSlice.actions.setIsError(false))
            })
        } catch (e) {
            console.log(e)
        }
    },
    logOut: () => dispatch => {
        try {
            dispatch(AuthActionCreator.setUser({}))
            dispatch(AuthActionCreator.setIsAuth(false))
            localStorage.removeItem('auth')
            localStorage.removeItem('user')
            localStorage.removeItem('token')
        } catch (e) {
            console.log(e)
        }
    }
}
