import {authSlice} from "./AuthSlice";
import {login, registration} from "../../../http/userAPI";

export const AuthActionCreator = {
    setIsAuth: (isAuth) => dispatch => {
        dispatch(authSlice.actions.setIsAuth(isAuth))
    },
    setUser: (user) => dispatch => {
        dispatch(authSlice.actions.setUser(user))
    },
    singIn: (email, password) => dispatch => {
        try {
            registration(email, password).then(
                dispatch(authSlice.actions.setIsError(false))
            )
        } catch (e) {
            dispatch(authSlice.actions.setRegMessage(e.response.data.message))
            dispatch(authSlice.actions.setIsError(true))
