import React, {useState} from 'react';
import {useActions} from "../../hooks/useActions";
import {useSelector} from "react-redux";

const CreateMemory = () => {
    const {isCategoryNameModal, categories} = useSelector(state => state.memoryReducer)
    const {addMemory, setIsCategoryNameModal} = useActions()

    const [categoryNameValue, setCategoryNameValue] = useState('')
    const [isWarning, setIsWarning] = useState(false)
    const [warningValue, setWarningValue] = useState('')

    const closeCategoryNameModal = () => {
        setIsCategoryNameModal(isCategoryNameModal)
        setCategoryNameValue('')
    }
    const confirmAddCategory = () => {
        for (let i = 0; i < categories.length; i++) {
            if (categories[i].title === categoryNameValue) {
                setWarningValue("Така назва вже існує")
                setIsWarning(true)
                return false
            }
        }
        if (!categoryNameValue) {
            setWarningValue("Це поле є обов'язковим")
            setIsWarning(true)
            return false
        }
        setIsWarning(false)
        addMemory(title, imgFile, selectedCategory.id)
        setCategoryNameValue('')
    }


    return (
        <div
            className={isCategoryNameModal ? 'memory-card__modal' : 'memory-card__modal closed'}
            onClick={closeCategoryNameModal}
        >
            <div
                className='content'
                onClick={e => e.stopPropagation()}
            >
                <div className='title'>
                    <h3>Як ви хочете назвати категорію?</h3>
                </div>
                <div className='form-box'>
                    <input
                        type='text'
                        placeholder={'Назва категорії'}
                        value={categoryNameValue}
                        onChange={e => setCategoryNameValue(e.target.value)}
                    />
                    <div className='btn-confirm-box'>
                        <div className={isWarning ? 'warning-text' : 'warning-text closed'}>
                            {warningValue}
                        </div>
                        <button
                            className='btn-confirm'
                            onClick={confirmAddCategory}
                        >
                            Підтвердити
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CreateMemory;
