import {$host} from "./index";
import axios from "axios";

export const fetchMemories = async (userId) => {
    const {data} = await $host.get(`api/memory/${userId}`)
    return data
}

export const fetchSelectedMemories = async (categoryId) => {
    const {data} = await $host.get(`api/memory/all/${categoryId}`)
    return data
}

export const uploadMemoryImg = async (file) => {
    const {data} = await axios.post('https://api.cloudinary.com/v1_1/malbo/image/upload', file)
    return data
}

