import React, {useState} from 'react';
import {useActions} from "../../hooks/useActions";
import {useSelector} from "react-redux";

const CreateCategory = () => {
    const {isCategoryNameModal} = useSelector(state => state.memoryReducer)
    const {user} = useSelector(state => state.authReducer)

    const {addCategory, setIsCategoryNameModal} = useActions()

    const [categoryNameValue, setCategoryNameValue] = useState('')

    return (
        <div
            className={isCategoryNameModal ? 'category-name__modal' : 'category-name__modal closed'}
            onClick={() => setIsCategoryNameModal(isCategoryNameModal)}
        >
            <div
                className='content'
                onClick={e => e.stopPropagation()}
            >
                <div className='title'>
                    <h3>Будь ласка, введіть назву категорії</h3>
                </div>
                <div className='form-box'>
                    <form>
                        <input
                            type='text'
                            value={categoryNameValue}
                            onChange={e => setCategoryNameValue(e.target.value)}
                        />
                        <button
                            className='btn-confirm'
                            type='submit'
                            onClick={() => addCategory(categoryNameValue, user.id)}
                        >
                            Підтвердити
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default CreateCategory;
