import React, {useState} from 'react';
import {useActions} from "../../hooks/useActions";
import {useNavigate} from "react-router-dom";
import {ROUTE_MEMORY} from "../../utils/consts";
import {useSelector} from "react-redux";

const LoginForm = () => {
    const navigate = useNavigate()
    const {user} = useSelector(state => state.authReducer)
    const {setEmail, setPassword, singIn, logIn} = useActions()

    const [emailValue, setEmailValue] = useState('')
    const [passwordValue, setPasswordValue] = useState('')
    const [haveAccount, setHaveAccount] = useState(true)

    const handleSubmitForm = (e) => {
        e.preventDefault()
        if(haveAccount) {
            logIn(emailValue, passwordValue).then()
            navigate(ROUTE_MEMORY)
        } else {
            singIn(emailValue, passwordValue)
        }
    }

    return (
        <div className='login-form'>
            <form onSubmit={handleSubmitForm}>
                <input

                    type='text'
                    autoComplete='on'
                    value={emailValue}
                    placeholder='Введіть свій email'
                    onChange={e => setEmailValue(e.target.value)}
                />
                <input
                    type='password'
                    autoComplete='on'
                    value={passwordValue}
                    placeholder='Введіть свій пароль'
                    onChange={e => setPasswordValue(e.target.value)}
                />
                <div className='btn-box'>
                    {haveAccount
                        ?
                        <div className='btn-box__text'>
                            <p>Немає облікового запису?
                                <span onClick={() => setHaveAccount(false)}>
                                    Зареєструйтесь!
                                </span>
                            </p>
                        </div>
                        :
                        <div className='btn-box__text'>
                            <p>У вас вже є обліковий запис?
                                <span onClick={() => setHaveAccount(true)}>
                                    Увійдіть!
                                </span>
                            </p>
                        </div>
                    }
                    {haveAccount
                        ?
                        <button type='submit'>Увійти</button>
                        :
                        <button type='submit'>Зареєструйтесь</button>
                    }
                </div>
            </form>
        </div>
    );
};

export default LoginForm;
