import React, {useEffect, useState} from 'react';
import MemoryItem from "../components/MemoryPage/MemoryItem";
import {useSelector} from "react-redux";
import {useActions} from "../hooks/useActions";
import MemorySelect from "../components/MemoryPage/MemorySelect";
import imgFish from "../img/fish.svg";
import imgFishBones from "../img/fish-bones.svg";
import imgCatBtnAdd from "../img/cat-btn-add.svg";
import imgBtnEdit from "../img/btn-edit.svg";
import {fetchCategories} from "../http/categoriesAPI";
import CreateCategory from "../components/modals/CreateCategory";

const MemoryPage = () => {
    const {
        categories,
        selectedCategory,
        selectedCategoryItems
    } = useSelector(state => state.memoryReducer)

    const {
        user
    } = useSelector(state => state.authReducer)

    const {
        setCategories,
        setSelectedCategory,
        setSelectedCategoryName,
        removeCategory,
        addItemInCategory,
        sortSelectedCategoryItems,
    } = useActions()

    const [isEdit, setIsEdit] = useState(false)
    const [isCategoryNameModal, setIsCategoryNameModal] = useState(false)

    // const fetchItems = useCallback(() => {
    //     const newArray = []
    //     for (let i = 0; i < categoryItems.length; i++) {
    //         if (categoryItems[i].category === selectedCategory.id) {
    //             newArray.push(categoryItems[i])
    //         }
    //     }
    //     setSelectedCategoryItems(newArray)
    // }, [selectedCategory, categoryItems])

    useEffect(() => {
        fetchCategories(user.id).then(data => setCategories(data))
        // fetchItems()
    }, [categories])


    return (
        <div className='memory-page'>
            <div className='memories-book'>
                <div className='categories-bar'>
                    <div className='categories-list'>
                        <div className='title'>
                            {categories
                                ?
                                <div><h3>Ваш список</h3></div>
                                :
                                <div><h3>Ваш список пуст</h3></div>
                            }
                        </div>
                        <div className='categories'>
                            {categories.map(category =>
                                <div
                                    className={category.id === selectedCategory.id ? 'category-name active' : 'category-name'}
                                    key={category.id}
                                    onClick={() => setSelectedCategory(category)}
                                >
                                    <img
                                        src={category.id === selectedCategory.id ? imgFishBones : imgFish}
                                        alt=''
                                        width={45}
                                        height={45}
                                    />
                                    <div className='text'>
                                        {category.title
                                            ?
                                            category.title
                                            :
                                            'Назва категорії'
                                        }
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                    <div className='btn-add-box'>
                        <div className='btn-and-cat'>
                            <img
                                src={imgCatBtnAdd}
                                alt=''
                            />
                            <button
                                className='btn-add'
                                onClick={() => setIsCategoryNameModal(!isCategoryNameModal)}
                            >
                                Додати
                            </button>
                        </div>
                    </div>
                </div>
                <div className='memories-bar'>
                    {selectedCategory.id
                        ?
                        <div className='title'>
                            <MemorySelect
                                defaultValue='Сортування'
                                onChange={sortSelectedCategoryItems}
                            />
                            <div className='name'>
                                <input
                                    type='text'
                                    placeholder='Назва категорії'
                                    maxLength={22}
                                    value={selectedCategory.title}
                                    onChange={e => setSelectedCategoryName(e.target.value)}
                                />
                            </div>
                            <div className='btn-box'>
                                <div
                                    className='btn-edit'
                                    onClick={() => setIsEdit(!isEdit)}
                                >
                                    <img src={imgBtnEdit} alt='' />
                                </div>
                                <div
                                    className={isEdit ? 'btn-delete' : 'btn-delete closed'}
                                    onClick={() => removeCategory(selectedCategory.id)}
                                >
                                   Видалити
                                </div>
                            </div>
                        </div>
                        :
                        <div className='title'>
                            <div className='name'>
                                Ви не обрали жодної категорії
                            </div>
                        </div>
                    }
                    {selectedCategory.id
                        &&
                        <div className='memories-list'>
                            <div className='memories'>
                                {selectedCategoryItems.map(item =>
                                    <MemoryItem
                                        key={item.id}
                                        item={item}
                                        selectedCategoryName={selectedCategory.name}
                                    />
                                )}
                            </div>
                            <div className='btn-add-box'>
                                <button
                                    className='btn-add'
                                    onClick={() => addItemInCategory(selectedCategory.id)}
                                >
                                    +
                                </button>
                            </div>
                        </div>
                    }
                </div>
            </div>
            <CreateCategory
                isCategoryNameModal={isCategoryNameModal}
            />
        </div>
    );
};

export default MemoryPage;
