import React, {useState} from 'react';
import {useActions} from "../../hooks/useActions";
import {useNavigate} from "react-router-dom";
import {ROUTE_MEMORY} from "../../utils/consts";
import {useSelector} from "react-redux";

const LoginForm = () => {
    const navigate = useNavigate()
    const {email, password} = useSelector
    const {setEmail, setPassword, singIn} = useActions()
    const [haveAccount, setHaveAccount] = useState(true)

    const {logIn} = useActions()

    function logInAc() {
        logIn()
        navigate(ROUTE_MEMORY)
    }

    return (
        <div className='login-form'>
            <form>
                <input
                    type='text'
                    value={email}
                    placeholder='Введіть свій email'
                    onChange={e => setEmail(e.target.value)}
                />
                <input
                    type='password'
                    value={password}
                    placeholder='Введіть свій пароль'
                    onChange={e => setPassword(e.target.value)}
                />
                <div className='btn-box'>
                    {haveAccount
                        ?
                        <div className='btn-box__text'>
                            <p>Немає облікового запису?
                                <span onClick={() => setHaveAccount(false)}>
                                    Зареєструйтесь!
                                </span>
                            </p>
                        </div>
                        :
                        <div className='btn-box__text'>
                            <p>У вас вже є обліковий запис?
                                <span onClick={() => setHaveAccount(true)}>
                                    Увійдіть!
                                </span>
                            </p>
                        </div>
                    }
                    {haveAccount
                        ?
                        <button onClick={logInAc}>Увійти</button>
                        :
                        <button onClick={logInAc}>Зареєструйтесь</button>
                    }
                </div>
            </form>
        </div>
    );
};

export default LoginForm;
