import React from 'react';
import {useNavigate} from "react-router-dom";
import {ROUTE_HOME, ROUTE_LOGIN, ROUTE_MEMORY} from "../../utils/consts";
import {useSelector} from "react-redux";
import {useActions} from "../../hooks/useActions";
import imgUserLogo from "../../img/user.svg";
import imgCatWithBall from "../../img/cat-header.svg";
import imgYarnBall from "../../img/yarn-ball.svg";

const Navbar = ({isOpenMenu}) => {
    const navigate = useNavigate()
    const isAuth = useSelector(state => state.authReducer.isAuth)
    const {logOut} = useActions()

    function logOutAc() {
        logOut()
        navigate(ROUTE_LOGIN)
    }

    return (
        isAuth
            ?
            <div className={isOpenMenu ? 'navbar' : 'navbar closed'}>
                <div className='left-nav'>
                    <div className='nav' onClick={() => navigate(ROUTE_HOME)}>Додому</div>
                    <div className='nav' onClick={() => navigate(ROUTE_MEMORY)}>Спогади</div>
                </div>
                <div className='right-nav'>
                    <div className='user-logo'>
                        <img src={imgUserLogo} alt='' />
                    </div>
                    <div className='nav' onClick={logOutAc}>Вийти</div>
                </div>
                <div className='header__border-bottom'>
                    <div className='cat-and-yarn-ball'>
                        <div className='cat'>
                            <img src={imgCatWithBall} alt='' />
                        </div>
                        <div className='yarn-ball'>
                            <img src={imgYarnBall} alt='' />
                        </div>
                    </div>
                    <hr className='yarn'/>
                </div>
            </div>
            :
            <div className={isOpenMenu ? 'navbar' : 'navbar closed'}>
            <div className='left-nav'>
                    <div className='nav' onClick={() => navigate(ROUTE_HOME)}>Додому</div>
                </div>
                <div className='right-nav'>
                    <div className='user-logo'>
                        <img src={imgUserLogo} alt='' />
                    </div>
                    <div className='nav' onClick={() => navigate(ROUTE_LOGIN)}>Войти</div>
                </div>
            </div>
    );
};

export default Navbar;
