import React, {useState} from 'react';
import {useActions} from "../../hooks/useActions";
import {useSelector} from "react-redux";

const CreateCategory = ({isCategoryNameModal}) => {
    const [categoryNameValue, setCategoryNameValue] = useState('')

    const {user} = useSelector(state => state.authReducer)

    const {addCategory} = useActions()

    return (
        <div className={isCategoryNameModal ? 'category-name__modal' : 'category-name__modal closed'}>
            <div className='content'>
                <div>
                    <h3>Будь ласка, введіть назву категорії</h3>
                </div>
                <div>
                    <form>
                        <input
                            type='text'
                            value={categoryNameValue}
                            onChange={e => setCategoryNameValue(e.target.value)}
                        />
                        <button
                            onClick={() => addCategory(categoryNameValue, user.id)}
                        >
                            Підтвердити
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default CreateCategory;
