import React, {useCallback, useEffect} from 'react';
import {useSelector} from "react-redux";
import {useActions} from "../hooks/useActions";
import ItemRating from "../components/ItemPage/ItemRating";


const ItemPage = () => {
    const {selectedCategoryItem, selectedMemories} = useSelector(state => state.memoryReducer)
    const {
        setSelectedCategoryItem,
        setSelectedCategoryItemName,
        setSelectedCategoryItemImg,
        setSelectedCategoryItemDescription,
    } = useActions()

    const fetchSelectedItem = useCallback(() => {
        for (let i = 0; i < selectedMemories.length; i++){
            if(selectedMemories[i].id === Number(localStorage.getItem('itemId'))){
                setSelectedCategoryItem(selectedMemories[i])
            }
        }
    }, [selectedMemories, selectedCategoryItem])

    useEffect(() => {
        fetchSelectedItem()
    }, [fetchSelectedItem])

    return (
        <div className='item-page'>
            <div className='item-card'>
                <div className='item-card__content-top'>
                    <div className='content-top__left'>
                        <img
                            src={selectedCategoryItem.img}
                            alt={selectedCategoryItem.name}
                        />
                        <input
                            type='file'
                            accept=".jpg, .jpeg, .png, .gif"
                            onChange={e => setSelectedCategoryItemImg(e.target.files)}
                        />
                    </div>
                    <div className='content-top__right'>
                        <div className='item-name'>
                            <textarea
                                value={selectedCategoryItem.name}
                                onChange={e => setSelectedCategoryItemName(e.target.value)}
                            />
                        </div>
                        <ItemRating />
                    </div>
                </div>
                <div className='item-card__content-bottom'>
                    <div className='description'>
                        <textarea
                            value={selectedCategoryItem.description}
                            onChange={e => setSelectedCategoryItemDescription(e.target.value)}
                        />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ItemPage;
